// Set payload to a 3 decimal number as string
msg.payload = parseFloat(msg.payload).toFixed(3);

// Create YYYY-MM-DD value from datetime string
var datetime = msg.data.last_updated;
msg.date = datetime.split('T')[0];

return msg;

// Your timestamp
let timestamp = msg.sunData.new_state.attributes.next_dusk;

// Convert to a JavaScript Date object
let date = new Date(timestamp);

// Get the epoch time in milliseconds
let epochTime = date.getTime();

// Convert to seconds (optional)
let epochTimeInSeconds = Math.floor(epochTime / 1000);

// Output the epoch time
msg.dusk_time = epochTimeInSeconds;
return msg;

// Get the current time as a Date object
let now = new Date();

// Get the current epoch time in milliseconds
let epochTime = now.getTime();

// Convert to seconds (optional)
let epochTimeInSeconds = Math.floor(epochTime / 1000);

// Output the epoch time
msg.current_time = epochTimeInSeconds;
return msg;

let delay_seconds = msg.dusk_time - msg.current_time
msg.delay = delay_seconds * 1000 //Miliseconds
return msg;

// The provided JSON array
let jsonArray = msg.messages

// Function to count words
/**
* @param {string} s
*/
function countWords(s) {
    return s.split(/\s+/).length;
}

// Processing the JSON array
let resultArray = [];
let title = null;
let text = "";

for (let i = 0; i < jsonArray.length; i++) {
    let item = jsonArray[i];
    if (countWords(item) < 10) {
        if (title) {
            resultArray.push(title);
            if (text) {
                resultArray.push(text.trim());
                text = "";
            }
        }
        title = item;
    } else {
        text += item + " ";
    }
}

// If there is a dangling title or text
if (title) {
    resultArray.push(title);
    if (text) {
        resultArray.push(text.trim());
    }
}

msg.messages = resultArray;
return msg;

