// Function to count words
/**
* @param {string} s
*/
function countWords(s) {
    return s.split(/\s+/).length;
}

// Processing the JSON array
let resultArray = [];
let title = null;
let text = "";

for (let i = 0; i < jsonArray.length; i++) {
    let item = jsonArray[i];
    if (countWords(item) < 10) {
        if (title) {
            resultArray.push(title);
            if (text) {
                resultArray.push(text.trim());
                text = "";
            }
        }
        title = item;
    } else {
        text += item + " ";
    }
}

// If there is a dangling title or text
if (title) {
    resultArray.push(title);
    if (text) {
        resultArray.push(text.trim());
    }
}

msg.messages = resultArray;
return msg;

