const today = msg.payload.status.serverTime;

// Go to the first day of the current month
const firstDayCurrentMonth = new Date(today.getFullYear(), today.getMonth(), 1);

// Previous month = current month - 1
const prevMonth = firstDayCurrentMonth.getMonth() - 1;
const yearOfPrevMonth = prevMonth < 0 ? today.getFullYear() - 1 : today.getFullYear();
const monthOfPrevMonth = (prevMonth + 12) % 12;

// First day of previous month
const firstDayPrevMonth = new Date(yearOfPrevMonth, monthOfPrevMonth, 1);

// Last day of previous month
const lastDayPrevMonth = new Date(yearOfPrevMonth, monthOfPrevMonth + 1, 0); // day 0 = last day of previous month

// Format as string
const formatDate = (d) => d.toISOString().split('T')[0] + " 00:00:00"; // start of day
const formatEndDate = (d) => d.toISOString().split('T')[0] + " 23:59:59"; // end of day

